mapper.update:
UPDATE solarnet.sn_power_datum SET node_id = #{nodeId},
source_id = #{sourceId},
price_loc_id = #{locationId}, watts = #{watts}, bat_volts = #{batteryVolts}, bat_amp_hrs = #{batteryAmpHours}, watt_hour = #{wattHourReading} WHERE id = #{id}

mapper.select:
SELECT

FROM
	solarnet.sn_power_datum pdatum
WHERE
	pdatum.node_id = #{id,javaType=long,jdbcType=BIGINT}
	AND pdatum.created = #{date,jdbcType=TIMESTAMP}

SELECT
FROM solarnet.sn_power_datum_most_recent recent INNER JOIN solarnet.sn_power_datum pdatum ON pdatum.id = recent.datum_id WHERE recent.node_id = #{node,javaType=long,jdbcType=BIGINT}
AND recent.source_id = #{source,javaType=string,jdbcType=VARCHAR}
ORDER BY pdatum.source_id

