mapper.select:
SELECT DISTINCT source_id FROM solarrep.rep_power_datum_daily WHERE node_id = #{node,javaType=long,jdbcType=BIGINT}
AND created_day >= #{start,javaType=org.joda.time.LocalDate,jdbcType=DATE}::date
AND created_day <= #{end,javaType=org.joda.time.LocalDate,jdbcType=DATE}::date

mapper.sql:
FROM
	solarnet.sn_power_datum pdatum



			AND pdatum.source_id IN

				#{source,javaType=string,jdbcType=VARCHAR}



			AND pdatum.node_id IN

				#{node,javaType=long,jdbcType=BIGINT}



			AND pdatum.created >= #{filter.startDate,javaType=org.joda.time.DateTime,jdbcType=TIMESTAMP}


			AND pdatum.created < #{filter.endDate,javaType=org.joda.time.DateTime,jdbcType=TIMESTAMP}

FROM solarrep.rep_power_datum_hourly rpdatum
AND rpdatum.source_id IN
#{source,javaType=string,jdbcType=VARCHAR}
AND rpdatum.node_id IN
#{node,javaType=long,jdbcType=BIGINT}
AND rpdatum.created_hour >= solarnet.get_node_local_timestamp( #{filter.startDate,javaType=org.joda.time.DateTime,jdbcType=TIMESTAMP}, #{filter.nodeId,javaType=long,jdbcType=BIGINT})
AND rpdatum.created_hour >= #{filter.startDate,javaType=org.joda.time.DateTime,jdbcType=TIMESTAMP}
AND rpdatum.created_hour <= solarnet.get_node_local_timestamp( #{filter.endDate,javaType=org.joda.time.DateTime,jdbcType=TIMESTAMP}, #{filter.nodeId,javaType=long,jdbcType=BIGINT})
AND rpdatum.created_hour < #{filter.endDate,javaType=org.joda.time.DateTime,jdbcType=TIMESTAMP}

FROM solarrep.rep_power_datum_daily rpdatum
AND rpdatum.source_id IN
#{source,javaType=string,jdbcType=VARCHAR}
AND rpdatum.node_id IN
#{node,javaType=long,jdbcType=BIGINT}
AND rpdatum.created_hour >= solarnet.get_node_local_timestamp( #{filter.startDate,javaType=org.joda.time.DateTime,jdbcType=TIMESTAMP}, #{filter.nodeId,javaType=long,jdbcType=BIGINT})
AND rpdatum.created_hour >= #{filter.startDate,javaType=org.joda.time.DateTime,jdbcType=TIMESTAMP}
AND rpdatum.created_hour <= solarnet.get_node_local_timestamp( #{filter.endDate,javaType=org.joda.time.DateTime,jdbcType=TIMESTAMP}, #{filter.nodeId,javaType=long,jdbcType=BIGINT})
AND rpdatum.created_hour < #{filter.endDate,javaType=org.joda.time.DateTime,jdbcType=TIMESTAMP}
